SELECT
	`ecs_student_account`.`uid`,
	`ecs_student_account`.`user_name` AS `phone`,
	`ecs_pay_student`.`pay_way`,
	`ecs_repayment_gather`.`repayment_day`,
	`ecs_repayment_gather`.`cal_repayment_day`,
	`ecs_repayment_gather`.`cal_repayment_money`,
	`ecs_repayment_gather`.`repay_id`,
	`ecs_pay_student`.`data`,
	`ecs_pay_student`.`card`
FROM
	`ecs_repayment_gather`
INNER JOIN `ecs_student_account` ON `ecs_repayment_gather`.`uid` = `ecs_student_account`.`uid`
INNER JOIN `ecs_pay_student` on `ecs_pay_student`.`uid` = `ecs_repayment_gather`.`uid`
WHERE
	`ecs_repayment_gather`.`cal_repayment_day` = '2016-01-20'
AND `ecs_repayment_gather`.`status` != '3'
AND `ecs_repayment_gather`.`cal_repayment_money` != '0'
AND `ecs_pay_student`.`pay_way` = 'withholding'
AND `ecs_pay_student`.`status` = '2'
AND `ecs_repayment_gather`.`uid` = '34483'
GROUP BY
	`ecs_student_account`.`uid`

















